mapper.select:
SELECT * FROM ebook
 WHERE category=#{category} 
 ORDER BY title

SELECT bseq, regdate, title, author, price, price_rent, useyn, likeyn
SELECT bseq, regdate, title, author, price_rent, price, useyn
FROM ebook WHERE title LIKE '%'||#{title}||'%' ORDER BY bseq DESC
SELECT bseq, regdate, title, author, price, price_rent, useyn, likeyn FROM (SELECT row_number() over(ORDER BY title) rn, bseq, regdate, title, price, price_rent, useyn, likeyn FROM ebook WHERE title LIKE '%'||#{title}||'%') WHERE rn <= #{criteria.pageNum} * #{criteria.rowsPerPage} AND rn > (#{criteria.pageNum}-1) * #{criteria.rowsPerPage}
INSERT INTO ebook(bseq, title,author, category, price_rent, price, content, image) VALUES(ebook_seq.NEXTVAL, #{title},#{author}, #{category}, #{price_rent}, #{price}, #{content}, #{image})
UPDATE ebook SET title=#{title},author=#{author}, category=#{category}, price_rent=#{price_rent}, price=#{price}, content=#{content}, image=#{image}, useyn=#{useyn}, likeyn=#{likeyn} WHERE bseq=#{bseq}
DELETE FROM ebook WHERE bseq=#{bseq}
SELECT title, SUM(quantity) as sales_count FROM order_view WHERE result='y' GROUP BY title
