mapper.select:
SELECT * FROM ebook
 WHERE category=#{category} 
 ORDER BY title


	SELECT bseq, regdate, title, author, price, price_rent, useyn, likeyn
      FROM  (SELECT row_number() over(ORDER BY title) rn, bseq, regdate, title, price, price_rent, useyn, likeyn
		       FROM ebook
		      WHERE title LIKE '%'||#{title}||'%') 
     WHERE rn <= #{criteria.pageNum} * #{criteria.rowsPerPage} 
       AND rn > (#{criteria.pageNum}-1) * #{criteria.rowsPerPage}

mapper.update:

UPDATE ebook 
SET title=#{title},author=#{author},
	category=#{category}, price_rent=#{price_rent}, price=#{price},
	content=#{content}, image=#{image}, useyn=#{useyn}, likeyn=#{likeyn}
WHERE bseq=#{bseq}
